Astrobj.InlineModule:
import math
import gyoto
class FlaredDisk:
    vel2=gyoto.array_double(4)
    coord2=gyoto.array_double(4)
    opening=0.2
    rin=4
    rout=15
    def __call__(self, coord):
        r=math.sin(coord[2])*coord[1]
        h_r=abs(math.cos(coord[2]))
        return max(h_r-self.opening, self.rin-r, r-self.rout)
    def getVelocity(self, coord, vel):
        for i in range(0, 4):
            self.coord2[i]=coord[i]
        self.this.metric().circularVelocity(self.coord2, self.vel2)
        for i in range(0, 4):
            vel[i]=self.vel2[i]
